import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import axios from "axios";
import "./BookingForm.css";

const BookingForm = () => {
  const navigate = useNavigate(); // Initialize navigate function
  const [form, setForm] = useState({
    name: "",
    contact: "",
    reason: "",
    date: "",
    time: "",
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [bookedSlots, setBookedSlots] = useState({});
  const [availableDates, setAvailableDates] = useState([]);
  const [availableTimes, setAvailableTimes] = useState([]);
  const [showTimePopup, setShowTimePopup] = useState(false);

  const timeOptions = [
    "9:00 AM", "9:30 AM", "10:00 AM", "10:30 AM", "11:00 AM", "11:30 AM",
    "12:00 PM", "12:30 PM", "3:00 PM", "3:30 PM", "4:00 PM", "4:30 PM", "5:00 PM",
  ];

  useEffect(() => {
    axios.get("http://localhost:5000/api/booked-slots")
      .then((response) => setBookedSlots(response.data))
      .catch((error) => console.error("Error fetching booked slots:", error));

    axios.get("http://localhost:5000/api/available-dates")
      .then((response) => setAvailableDates(response.data))
      .catch((error) => console.error("Error fetching available dates:", error));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    setError("");

    try {
      const response = await axios.post("http://localhost:5000/api/appointments", form);
      if (response && response.data) {
        setMessage("✅ Appointment booked successfully!");
        setTimeout(() => setMessage(""), 4000);
        setForm({ name: "", contact: "", reason: "", date: "", time: "" });
      }
    } catch (err) {
      setError(err.response?.data.message || "❌ Failed to book appointment.");
      setTimeout(() => setError(""), 4000);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="booking-form-container">
      {/* My Appointments Button */}
      <button className="my-appointments-button" onClick={() => navigate("/my-appointments")}>
        My Appointments
      </button>

      <div className="booking-form-wrapper">
        <h1 className="booking-form-title">Appointment Booking System</h1>
        <h2 className="booking-form-subtitle">Book an Appointment</h2>

        {message && <div className="booking-form-message success">{message}</div>}
        {error && <div className="booking-form-message error">{error}</div>}

        <form onSubmit={handleSubmit} className="booking-form">
          <div className="form-group">
            <label>Client Name</label>
            <input type="text" name="name" value={form.name} placeholder="Enter your name" required
              onChange={(e) => setForm({ ...form, name: e.target.value })} />
          </div>

          <div className="form-group">
            <label>Contact</label>
            <input type="text" name="contact" value={form.contact} placeholder="Enter contact number" required
              onChange={(e) => setForm({ ...form, contact: e.target.value })} />
          </div>

          <div className="form-group">
            <label>Reason for Booking</label>
            <input type="text" name="reason" value={form.reason} placeholder="Booking Reason" required
              onChange={(e) => setForm({ ...form, reason: e.target.value })} />
          </div>

          <div className="form-group">
            <label>Appointment Date</label>
            <input type="date" name="date" value={form.date} required
              onChange={(e) => setForm({ ...form, date: e.target.value })} />
          </div>

          <div className="form-group">
            <label>Time Slot</label>
            <input type="text" name="time" value={form.time} readOnly placeholder="Click to select a time slot" required
              onClick={() => setShowTimePopup(true)} />
          </div>

          {showTimePopup && (
            <div className="time-slot-popup">
              {timeOptions.map((time) => (
                <button key={time} className="time-slot" onClick={() => setForm({ ...form, time })}>
                  {time}
                </button>
              ))}
            </div>
          )}

          <button type="submit" className="booking-form-button" disabled={loading || !form.time}>
            {loading ? "Booking..." : "Book Appointment"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingForm;
