import React, { useState, useEffect } from "react";
import axios from "axios";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "./BookingForm.css";

const BookingForm = () => {
  const [form, setForm] = useState({
    name: "",
    contact: "",
    date: "",
    time: "",
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [bookedSlots, setBookedSlots] = useState({});
  const [availableDates, setAvailableDates] = useState([]);
  const [availableTimes, setAvailableTimes] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);

  // Define available time slots
  const timeOptions = [
    "9:00 AM", "9:15 AM", "9:30 AM", "9:45 AM",
    "10:00 AM", "10:15 AM", "10:30 AM", "10:45 AM",
    "11:00 AM", "11:15 AM", "11:30 AM", "11:45 AM",
    "12:00 PM", "12:15 PM", "12:30 PM", "12:45 PM",
  ];

  useEffect(() => {
    // Fetch booked slots and available dates on component mount
    axios
      .get("http://localhost:5000/api/booked-slots")
      .then((response) => setBookedSlots(response.data))
      .catch((error) => console.error("Error fetching booked slots:", error));

    axios
      .get("http://localhost:5000/api/available-dates")
      .then((response) => setAvailableDates(response.data))
      .catch((error) => console.error("Error fetching available dates:", error));
  }, []);

  const handleDateChange = (date) => {
    setSelectedDate(date);
    const formattedDate = date.toISOString().split("T")[0];
    setForm({ ...form, date: formattedDate });

    // Filter available time slots based on booked slots
    const bookedTimes = bookedSlots[formattedDate]?.map((slot) => slot.time) || [];
    const available = timeOptions.filter((time) => !bookedTimes.includes(time));
    setAvailableTimes(available);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    setError("");

    try {
      const response = await axios.post("http://localhost:5000/api/appointments", form);
      if (response.data) {
        setMessage("✅ Appointment booked successfully!");
        setBookedSlots((prev) => ({
          ...prev,
          [form.date]: [...(prev[form.date] || []), form],
        }));
        setForm({ name: "", contact: "", date: "", time: "" });
      }
    } catch (err) {
      setError("❌ Failed to book appointment. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="booking-form-container">
      <div className="booking-form-wrapper">
        <h1 className="booking-form-title">Appointment Booking</h1>

        {message && <div className="booking-form-message success">{message}</div>}
        {error && <div className="booking-form-message error">{error}</div>}

        <form onSubmit={handleSubmit} className="booking-form">
          <div className="form-group">
            <label>Client Name</label>
            <input
              type="text"
              value={form.name}
              onChange={(e) => setForm({ ...form, name: e.target.value })}
              placeholder="Enter your name"
              required
            />
          </div>

          <div className="form-group">
            <label>Contact</label>
            <input
              type="text"
              value={form.contact}
              onChange={(e) => setForm({ ...form, contact: e.target.value })}
              placeholder="Enter your contact number"
              required
            />
          </div>

          <div className="form-group">
            <label>Appointment Date</label>
            <Calendar
              onChange={handleDateChange}
              value={selectedDate}
              tileDisabled={({ date }) =>
                !availableDates.includes(date.toISOString().split("T")[0])
              }
            />
          </div>

          {selectedDate && (
            <div className="form-group">
              <label>Available Time Slots</label>
              <div className="time-slots">
                {availableTimes.length > 0 ? (
                  availableTimes.map((time) => (
                    <button
                      key={time}
                      type="button"
                      className={`time-slot ${form.time === time ? "selected" : ""}`}
                      onClick={() => setForm({ ...form, time })}
                    >
                      {time}
                    </button>
                  ))
                ) : (
                  <p>No available time slots for this date.</p>
                )}
              </div>
            </div>
          )}

          <button type="submit" className="booking-form-button" disabled={loading || !form.time}>
            {loading ? "Booking..." : "Book Appointment"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingForm;
