import React, { useState } from "react";
import axios from "axios";
import "./Register.css"; // ✅ Import the CSS file

const Register = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    age: "",
    city: "",
    email: "",
    password: "",
  });

  const [passwordValid, setPasswordValid] = useState(false);
  const [passwordMessage, setPasswordMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === "password") {
      // Password validation logic
      const passwordRegex = /^(?=.*[a-zA-Z])(?=.*\d)(?=.*[A-Z])(?=.*[a-z])(?=\S).*$/;
      const minMaxLength = value.length >= 4 && value.length <= 6;
      const isValidPassword = passwordRegex.test(value) && minMaxLength;

      if (isValidPassword) {
        setPasswordValid(true);
        setPasswordMessage(""); // Clear message on valid password
      } else {
        setPasswordValid(false);
        setPasswordMessage("Password must be 4-6 characters long, contain at least one number, one uppercase letter, one lowercase letter, and no spaces.");
      }
    }

    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);  // Log form data before submitting
  
    try {
      const response = await axios.post("http://localhost:5000/api/register", formData);
      alert(response.data.message);
    } catch (error) {
      console.error('Error during registration:', error);
      alert(error.response?.data?.error || 'Error registering user');
    }
  };

  return (
    <div className="register-form-container">
      <h2 className="register-form-title">Register</h2>
      <form className="register-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Full Name</label>
          <input type="text" name="fullName" placeholder="Enter your full name" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Age</label>
          <input type="number" name="age" placeholder="Enter your age" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>City</label>
          <input type="text" name="city" placeholder="Enter your city" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input type="email" name="email" placeholder="Enter your email" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input type="password" name="password" placeholder="Enter your password" onChange={handleChange} required />
          <div className="password-rules">
            <p>{passwordMessage}</p>
          </div>
        </div>
        <button className="register-form-button" type="submit" disabled={!passwordValid}>Register</button>
      </form>
    </div>
  );
};

export default Register;
