import React, { useState, useEffect } from "react";
import axios from "axios";
import "./BookingForm.css";

const BookingForm = () => {
  const [form, setForm] = useState({
    name: "",
    contact: "",
    date: "",
    time: "",
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [bookedSlots, setBookedSlots] = useState({});
  const [availableDates, setAvailableDates] = useState([]);
  const [availableTimes, setAvailableTimes] = useState([]);
  const [showTimePopup, setShowTimePopup] = useState(false);

  // Define available time slots (excluding 1:00 PM to 2:30 PM)
  const timeOptions = [
    "9:00 AM",
    "9:30 AM",
    "10:00 AM",
    "10:30 AM",
    "11:00 AM",
    "11:30 AM",
    "12:00 PM",
    "12:30 PM",
    "3:00 PM",
    "3:30 PM",
    "4:00 PM",
    "4:30 PM",
    "5:00 PM",
  ];

  // Fetch booked slots and available dates on component mount
  useEffect(() => {
    axios
      .get("http://localhost:5000/api/booked-slots")
      .then((response) => setBookedSlots(response.data))
      .catch((error) => console.error("Error fetching booked slots:", error));

    axios
      .get("http://localhost:5000/api/available-dates")
      .then((response) => setAvailableDates(response.data))
      .catch((error) =>
        console.error("Error fetching available dates:", error)
      );
  }, []);

  // Validate phone number: check if the contact number is already used by another user
  const validatePhoneNumber = (phoneNumber) => {
    for (let date in bookedSlots) {
      if (bookedSlots[date].some((slot) => slot.contact === phoneNumber)) {
        return false; // The phone number is already used for a different user
      }
    }
    return true; // The phone number is available for booking
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage(""); // Reset the message
    setError(""); // Reset any error

    // Validate phone number before proceeding with the booking
    
    if (!validatePhoneNumber(form.contact)) {
      setError("âŒ This phone number has already been used by another user.");
      setLoading(false); // Ensure loading state is reset if there's an error
      setTimeout(() => setError(""), 4000); // Clear error message after 4 seconds
      return;
    }

    try {
      console.log("ðŸ”µ Sending appointment data:", form); // Debug log

      const response = await axios.post(
        "http://localhost:5000/api/appointments",
        form
      );

      console.log("ðŸŸ¢ Booking successful:", response.data); // Debug log

      if (response && response.data) {
        setMessage("âœ… Appointment booked successfully!");
        console.log("ðŸ”µ Message set:", "âœ… Appointment booked successfully!"); // Debug log

        setBookedSlots((prev) => ({
          ...prev,
          [form.date]: [...(prev[form.date] || []), form],
        }));
        setForm({ name: "", contact: "", date: "", time: "" });

        // Hide success message after 4 seconds
        setTimeout(() => setMessage(""), 4000);
      }
    } catch (err) {
      console.error("ðŸ”´ Booking Error:", err); // Debug log
      if (err.response) {
        console.log("ðŸ”´ Server Response Error:", err.response.data);
        setError(
          err.response.data.message ||
            "âŒ Failed to book appointment. Please try again later."
        );
      } else if (err.request) {
        console.log("ðŸ”´ Network Error:", err.request);
        setError("âŒ Network issue. Backend may not be running.");
      } else {
        console.log("ðŸ”´ Unknown Error:", err.message);
        setError("âŒ Unexpected error. Check console for details.");
      }

      // Hide error message after 4 seconds
      setTimeout(() => setError(""), 4000);
    } finally {
      setLoading(false); // Ensure the loading state is reset after the process
    }
  };

  // Handle date change
  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    setForm({ ...form, date: selectedDate });

    // Filter available time slots based on the selected date
    const bookedTimes = bookedSlots[selectedDate]
      ? bookedSlots[selectedDate].map((slot) => slot.time)
      : [];
    const available = timeOptions.filter((time) => !bookedTimes.includes(time));
    setAvailableTimes(available);
  };

  // Handle time slot selection
  const handleTimeSelect = (time) => {
    setForm({ ...form, time });
    setShowTimePopup(false); // Close the popup after selecting a time

    // Hide the time slot popup after 4 seconds
    setTimeout(() => setShowTimePopup(false), 4000);
  };

  return (
    <div className="booking-form-container">
      <div className="booking-form-wrapper">
        <h1 className="booking-form-title">Appointment Booking System</h1>
        <h2 className="booking-form-subtitle">Book an Appointment</h2>

        {message && (
          <div className="booking-form-message success">{message}</div>
        )}
        {error && <div className="booking-form-message error">{error}</div>}

        <form onSubmit={handleSubmit} className="booking-form">
          <div className="form-group">
            <label>Client Name</label>
            <input
              type="text"
              name="name"
              value={form.name}
              onChange={(e) => setForm({ ...form, name: e.target.value })}
              placeholder="Enter your name"
              required
            />
          </div>

          <div className="form-group">
            <label>Contact</label>
            <input
              type="text"
              name="contact"
              value={form.contact}
              onChange={(e) => setForm({ ...form, contact: e.target.value })}
              placeholder="Enter your contact number"
              required
            />
          </div>
          <div className="form-group">
            <label>Reason for Booking</label>
            <input
              type="text"
              name="reason"
              value={form.reason}
              onChange={(e) => setForm({ ...form, reason: e.target.value })}
              placeholder="Booking Reason"
              required
            />
          </div>

          <div className="form-group">
            <label>Appointment Date</label>
            <input
              type="date"
              name="date"
              value={form.date}
              onChange={handleDateChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Time Slot</label>
            <input
              type="text"
              name="time"
              value={form.time}
              readOnly
              onClick={() => setShowTimePopup(true)}
              placeholder="Click to select a time slot"
              required
            />
          </div>

          {showTimePopup && (
            <div className="time-slot-popup">
              {availableTimes.map((time) => (
                <button
                  key={time}
                  className="time-slot"
                  onClick={() => handleTimeSelect(time)}
                >
                  {time}
                </button>
              ))}
            </div>
          )}

          <button
            type="submit"
            className="booking-form-button"
            disabled={loading || !form.time}
          >
            {loading ? "Booking..." : "Book Appointment"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingForm;
