import React, { useState, useEffect } from "react";
import axios from "axios";
import "./BookingForm.css";

const BookingForm = () => {
  const [form, setForm] = useState({
    name: "",
    contact: "",
    date: "",
    time: "",
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [timeSlots, setTimeSlots] = useState([]);
  const [bookedSlots, setBookedSlots] = useState([]);
  const [showTimePopup, setShowTimePopup] = useState(false);

  const timeOptions = [
    "9:00 AM",
    "9:30 AM",
    "10:00 AM",
    "10:30 AM",
    "11:00 AM",
    "11:30 AM",
    "12:00 PM",
    "12:30 PM",
    "1:00 PM",
    "1:30 PM",
    "2:00 PM",
    "2:30 PM",
    "3:00 PM",
    "3:30 PM",
    "4:00 PM",
    "4:30 PM",
    "5:00 PM",
  ];

  // Fetch booked slots for the selected date
  useEffect(() => {
    const fetchBookedSlots = async () => {
      if (form.date) {
        try {
          const response = await axios.get(
            `http://localhost:5000/api/booked-slots?date=${form.date}`
          );
          setBookedSlots(response.data || []);
        } catch (err) {
          console.error("Error fetching booked slots:", err);
        }
      }
    };
    fetchBookedSlots();
  }, [form.date]);

  // Update form state when an input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prevForm) => ({
        ...prevForm,
        [name]: value,
    }));

    // Clear success or error message when user interacts with form
    setMessage("");
    setError("");
};

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    setError("");

    try {
      const response = await axios.post(
        "http://localhost:5000/api/appointments",
        form
      );
      if (response && response.data) {
        setMessage("Appointment booked successfully!");
        setForm({
          name: "",
          contact: "",
          date: "",
          time: "",
        });
        setBookedSlots([...bookedSlots, form.time]);
      } else {
        setError("Something went wrong! Please try again.");
      }
    } catch (err) {
      console.error("Error booking appointment:", err);
      setError("Failed to book appointment. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  // Handle time selection
  const handleTimeSelect = (time) => {
    setForm((prevForm) => ({
      ...prevForm,
      time,
    }));
    setShowTimePopup(false);
  };

  return (
    <div className="booking-form-container">
      <div className="booking-form-wrapper">
        <h1 className="booking-form-title">Appointment Booking System</h1>
        <h2 className="booking-form-subtitle">Book an Appointment</h2>

        {message && <div className="booking-form-message success">{message}</div>}
        {error && <div className="booking-form-message error">{error}</div>}

        <form onSubmit={handleSubmit} className="booking-form">
          <div className="form-group">
            <label>Full Name</label>
            <input
              type="text"
              name="name"
              value={form.name}
              onChange={handleChange}
              placeholder="Enter your name"
              required
            />
          </div>

          <div className="form-group">
            <label>Contact</label>
            <input
              type="text"
              name="contact"
              value={form.contact}
              onChange={handleChange}
              placeholder="Enter your contact number"
              required
            />
          </div>

          <div className="form-group">
            <label>Appointment Date</label>
            <input
              type="date"
              name="date"
              value={form.date}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Time Slot</label>
            <input
              type="text"
              name="time"
              value={form.time}
              readOnly
              onClick={() => setShowTimePopup(true)}
              placeholder="Click to select a time slot"
              required
            />
          </div>

          {showTimePopup && (
            <div className="time-slot-popup">
              {timeOptions.map((time) => (
                <button
                  key={time}
                  className={`time-slot ${
                    bookedSlots.includes(time) ? "booked" : ""
                  }`}
                  onClick={() => !bookedSlots.includes(time) && handleTimeSelect(time)}
                  disabled={bookedSlots.includes(time)}
                  title={bookedSlots.includes(time) ? "Sorry! Already Booked" : ""}
                >
                  {time}
                </button>
              ))}
            </div>
          )}

          <button
            type="submit"
            className="booking-form-button"
            disabled={loading || !form.time}
          >
            {loading ? "Booking..." : "Book Appointment"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingForm;
