import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./BookingForm.css";

const BookingForm = () => {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: "",
    contact: "",
    reason: "",
    date: "",
    time: "",
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [bookedSlots, setBookedSlots] = useState({});
  const [availableTimes, setAvailableTimes] = useState([]);
  const [showTimePopup, setShowTimePopup] = useState(false);

  useEffect(() => {
    axios.get("http://localhost:5000/api/booked-slots")
      .then(response => setBookedSlots(response.data))
      .catch(error => console.error("Error fetching booked slots:", error));

    const times = [];
    let startTime = new Date(0, 0, 0, 9, 0, 0);
    const endTime = new Date(0, 0, 0, 17, 0, 0);

    while (startTime <= endTime) {
      let hours = startTime.getHours();
      let minutes = startTime.getMinutes();
      let formattedTime = `${hours % 12 || 12}:${minutes === 0 ? "00" : minutes} ${hours < 12 ? "AM" : "PM"}`;
      times.push(formattedTime);
      startTime.setMinutes(startTime.getMinutes() + 30);
    }

    setAvailableTimes(times);
  }, []);

  const showError = (msg) => {
    setError(msg);
    setTimeout(() => setError(""), 4000);
  };

  const showSuccess = (msg) => {
    setMessage(msg);
    setTimeout(() => setMessage(""), 4000);
  };

  const validateName = (name) => /^[A-Za-z\s]+$/.test(name);
  const validateContact = (contact) => /^0\d{9}$/.test(contact);
  const validateReason = (reason) => /^[a-zA-Z0-9 ]*$/.test(reason);

  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    const day = new Date(selectedDate).getDay();

    if (day === 0 || day === 6) {
      showError("❌ Weekends are not available for booking.");
      setForm({ ...form, date: "" });
      return;
    }

    setForm({ ...form, date: selectedDate });
    const bookedForDate = bookedSlots[selectedDate] || [];
    const availableForDate = availableTimes.filter(time => !bookedForDate.includes(time));
    setAvailableTimes(availableForDate);
  };

  const handleTimeSelect = (time) => {
    setForm({ ...form, time });
    setShowTimePopup(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    setError("");

    if (!validateName(form.name)) {
      showError("❌ Name can only contain letters and spaces.");
      setLoading(false);
      return;
    }

    if (!validateContact(form.contact)) {
      showError("❌ Contact number must start with 0 and be 10 digits long.");
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/appointments", form);

      if (response && response.data) {
        showSuccess("✅ Appointment booked successfully!");
        setForm({ name: "", contact: "", reason: "", date: "", time: "" });
      }
    } catch (err) {
      showError("❌ Failed to book appointment. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="booking-form-container">
      <div className="booking-form-wrapper">
        <button className="my-appointments-button" onClick={() => navigate("/myappointment")}>
          My Appointments
        </button>
        <h1>Appointment Booking</h1>
        {message && <div className="success">{message}</div>}
        {error && <div className="error">{error}</div>}

        <form onSubmit={handleSubmit} className="booking-form">
          <div className="form-group">
            <label>Name</label>
            <input type="text" value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} required />
          </div>

          <div className="form-group">
            <label>Contact</label>
            <input type="text" value={form.contact} onChange={(e) => setForm({ ...form, contact: e.target.value })} required />
          </div>

          <div className="form-group">
            <label>Reason</label>
            <input type="text" value={form.reason} onChange={(e) => setForm({ ...form, reason: e.target.value })} required />
          </div>

          <div className="form-group">
            <label>Date</label>
            <input type="date" value={form.date} onChange={handleDateChange} required min={new Date().toISOString().split("T")[0]} />
          </div>

          <div className="form-group">
            <label>Time Slot</label>
            <input type="text" value={form.time} readOnly onClick={() => setShowTimePopup(true)} required />
          </div>

          {showTimePopup && (
            <div className="time-slot-popup">
              {availableTimes.map((time) => (
                <button key={time} onClick={() => handleTimeSelect(time)} disabled={bookedSlots[form.date]?.includes(time)}>
                  {time}
                </button>
              ))}
            </div>
          )}

          <button type="submit" disabled={loading || !form.time}>
            {loading ? "Booking..." : "Book Appointment"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingForm;
