import React, { useState, useEffect } from "react";
import axios from "axios";
import "./BookingForm.css";

const BookingForm = () => {
  const [form, setForm] = useState({ name: "", contact: "", date: "", time: "" });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [bookedSlots, setBookedSlots] = useState({});
  const [availableDates, setAvailableDates] = useState([]);
  const [availableTimes, setAvailableTimes] = useState([]);
  const [showTimePopup, setShowTimePopup] = useState(false);

  // Define available time slots (excluding 1:00 PM to 2:30 PM)
  const timeOptions = [
    "9:00 AM", "9:30 AM", "10:00 AM", "10:30 AM",
    "11:00 AM", "11:30 AM", "12:00 PM", "12:30 PM",
    "3:00 PM", "3:30 PM", "4:00 PM", "4:30 PM", "5:00 PM"
  ];

  // Fetch booked slots and available dates on component mount
  useEffect(() => {
    axios.get("http://localhost:5000/api/booked-slots")
      .then(response => setBookedSlots(response.data))
      .catch(error => console.error("Error fetching booked slots:", error));

    axios.get("http://localhost:5000/api/available-dates")
      .then(response => setAvailableDates(response.data))
      .catch(error => console.error("Error fetching available dates:", error));
  }, []);

  // Update available times when a date is selected
  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    setForm(prev => ({ ...prev, date: selectedDate, time: "" }));

    // Disable weekends
    const dateObj = new Date(selectedDate);
    if (dateObj.getDay() === 0 || dateObj.getDay() === 6) {
      setAvailableTimes([]);
      return;
    }

    // Filter out already booked times
    const bookedTimes = bookedSlots[selectedDate] || [];
    const availableTimesForDate = timeOptions.filter(time => !bookedTimes.includes(time));

    setAvailableTimes(availableTimesForDate);
  };

  // Handle time selection
  const handleTimeSelect = (time) => {
    setForm(prev => ({ ...prev, time }));
    setShowTimePopup(false); // Close the popup after selection
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    setError("");

    try {
      console.log("üîµ Sending appointment data:", form); // Debug log

      const response = await axios.post("http://localhost:5000/api/appointments", form);

      console.log("üü¢ Booking successful:", response.data); // Debug log

      if (response && response.data) {
        setMessage("‚úÖ Appointment booked successfully!");
        setBookedSlots(prev => ({
          ...prev,
          [form.date]: [...(prev[form.date] || []), form.time]
        }));
        setForm({ name: "", contact: "", date: "", time: "" });
      }
    } catch (err) {
      console.error("üî¥ Booking Error:", err); // Debug log
      if (err.response) {
        console.log("üî¥ Server Response Error:", err.response.data);
        setError(err.response.data.message || "‚ùå Failed to book appointment. Please try again later.");
      } else if (err.request) {
        console.log("üî¥ Network Error:", err.request);
        setError("‚ùå Network issue. Backend may not be running.");
      } else {
        console.log("üî¥ Unknown Error:", err.message);
        setError("‚ùå Unexpected error. Check console for details.");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="booking-form-container">
      <div className="booking-form-wrapper">
        <h1 className="booking-form-title">Appointment Booking System</h1>
        <h2 className="booking-form-subtitle">Book an Appointment</h2>

        {message && <div className="booking-form-message success">{message}</div>}
        {error && <div className="booking-form-message error">{error}</div>}

        <form onSubmit={handleSubmit} className="booking-form">
          <div className="form-group">
            <label>Full Name</label>
            <input type="text" name="name" value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} placeholder="Enter your name" required />
          </div>

          <div className="form-group">
            <label>Contact</label>
            <input type="text" name="contact" value={form.contact} onChange={(e) => setForm({ ...form, contact: e.target.value })} placeholder="Enter your contact number" required />
          </div>

          <div className="form-group">
            <label>Appointment Date</label>
            <input type="date" name="date" value={form.date} onChange={handleDateChange} required />
          </div>

          <div className="form-group">
            <label>Time Slot</label>
            <input type="text" name="time" value={form.time} readOnly onClick={() => setShowTimePopup(true)} placeholder="Click to select a time slot" required />
          </div>

          {showTimePopup && (
            <div className={`time-slot-popup ${!showTimePopup ? 'hidden' : ''}`}>
              {availableTimes.map((time) => (
                <button key={time} className="time-slot" onClick={() => handleTimeSelect(time)}>
                  {time}
                </button>
              ))}
            </div>
          )}

          <button type="submit" className="booking-form-button" disabled={loading || !form.time}>
            {loading ? "Booking..." : "Book Appointment"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingForm;
