// MyAppointments.js (Frontend)
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./MyAppointments.css";

const MyAppointments = () => {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleCancel = (appointmentId) => {
    axios
      .delete(`http://localhost:5000/api/appointments/${appointmentId}`)
      .then(() => {
        setAppointments(appointments.filter((appointment) => appointment._id !== appointmentId));
      })
      .catch((err) => {
        console.error("Error canceling appointment:", err);
        setError("Failed to cancel the appointment.");
      });
  };

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/appointments") // Updated to get all appointments correctly
      .then((response) => {
        setAppointments(response.data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching appointments:", err);
        setError("Failed to load appointments.");
        setLoading(false);
      });
  }, []);

  return (
    <div className="appointments-container">
      <h1>My Appointments</h1>
      <div className="outside-button-container">
        <button className="book-appointment-btn" onClick={() => navigate("/")}>Book New Appointment</button>
      </div>
      {loading && <p className="loading">Loading appointments...</p>}
      {error && <p className="error">{error}</p>}
      {!loading && appointments.length === 0 && <p className="no-appointments">No appointments found.</p>}
      {appointments.length > 0 && (
        <table className="appointments-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Contact</th>
              <th>Date</th>
              <th>Time</th>
              <th>Reason</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {appointments.map((appointment) => (
              <tr key={appointment._id}>
                <td>{appointment.name}</td>
                <td>{appointment.contact}</td>
                <td>{appointment.date}</td>
                <td>{appointment.time}</td>
                <td>{appointment.reason}</td>
                <td>
                  <button className="cancel-btn" onClick={() => handleCancel(appointment._id)}>
                    Cancel
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default MyAppointments;
