import React, { useState, useEffect } from "react";
import axios from "axios";
import "./BookingForm.css";

const BookingForm = () => {
  const [form, setForm] = useState({
    name: "",
    contact: "",
    reason: "",
    date: "",
    time: "",
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [bookedSlots, setBookedSlots] = useState({});
  const [availableTimes, setAvailableTimes] = useState([]);
  const [showTimePopup, setShowTimePopup] = useState(false);

  const timeOptions = [
    "9:00 AM", "9:30 AM", "10:00 AM", "10:30 AM", "11:00 AM",
    "11:30 AM", "12:00 PM", "12:30 PM", "3:00 PM", "3:30 PM",
    "4:00 PM", "4:30 PM", "5:00 PM"
  ];

  useEffect(() => {
    axios.get("http://localhost:5000/api/booked-slots")
      .then(response => setBookedSlots(response.data))
      .catch(error => console.error("Error fetching booked slots:", error));
  }, []);

  // 🔹 Validate name (Only letters and spaces)
  const validateName = (name) => /^[A-Za-z\s]+$/.test(name);

  // 🔹 Validate phone number (Starts with 0, exactly 10 digits)
  const validatePhoneNumber = (phoneNumber) => /^0\d{9}$/.test(phoneNumber);

  // 🔹 Validate reason (No special characters)
  const validateReason = (reason) => /^[a-zA-Z0-9 ]*$/.test(reason);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    setError("");

    if (!validateName(form.name)) {
      setError("❌ Name can only contain letters and spaces.");
      setLoading(false);
      return;
    }

    if (!validatePhoneNumber(form.contact)) {
      setError("❌ Phone number must start with 0 and be 10 digits long.");
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:5000/api/appointments", form
      );

      if (response && response.data) {
        setMessage("✅ Appointment booked successfully!");
        setForm({ name: "", contact: "", reason: "", date: "", time: "" });
      }
    } catch (err) {
      setError("❌ Failed to book appointment. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    const day = new Date(selectedDate).getDay();

    if (day === 0 || day === 6) {
      setError("❌ Weekends are not available.");
      setForm({ ...form, date: "" });
      return;
    } else {
      setForm({ ...form, date: selectedDate });
    }
  };

  const handleTimeSelect = (time) => {
    setForm({ ...form, time });
    setShowTimePopup(false);
  };

  return (
    <div className="booking-form-container">
      <div className="booking-form-wrapper">
        <h1>Appointment Booking</h1>
        {/* Button Outside the Table */}
      <div className="outside-button-container">
        <button className="book-appointment-btn" onClick={() => navigate("/my")}>
          Book New Appointment
        </button>
      </div>
        {message && <div className="success">{message}</div>}
        {error && <div className="error">{error}</div>}

        <form onSubmit={handleSubmit} className="booking-form">
          <div className="form-group">
            <label>Name</label>
            <input
              type="text"
              name="name"
              value={form.name}
              onChange={(e) => {
                if (validateName(e.target.value) || e.target.value === "") {
                  setForm({ ...form, name: e.target.value });
                } else {
                  setError("❌ Name can only contain letters and spaces.");
                  setTimeout(() => setError(""), 4000);
                }
              }}
              placeholder="Enter your name"
              required
            />
          </div>

          <div className="form-group">
            <label>Contact</label>
            <input
              type="text"
              name="contact"
              value={form.contact}
              onChange={(e) => {
                if (/^\d*$/.test(e.target.value)) {
                  setForm({ ...form, contact: e.target.value });
                } else {
                  setError("❌ Phone number must contain only numbers.");
                  setTimeout(() => setError(""), 4000);
                }
              }}
              placeholder="Enter your contact number"
              required
            />
          </div>

          <div className="form-group">
            <label>Reason</label>
            <input
              type="text"
              name="reason"
              value={form.reason}
              onChange={(e) => {
                if (validateReason(e.target.value)) {
                  setForm({ ...form, reason: e.target.value });
                } else {
                  setError("❌ Special characters are not allowed.");
                  setTimeout(() => setError(""), 4000);
                }
              }}
              placeholder="Booking Reason"
              required
            />
          </div>

          <div className="form-group">
            <label>Date</label>
            <input
              type="date"
              name="date"
              value={form.date}
              onChange={handleDateChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Time Slot</label>
            <input
              type="text"
              name="time"
              value={form.time}
              readOnly
              onClick={() => setShowTimePopup(true)}
              placeholder="Click to select a time slot"
              required
            />
          </div>

          {showTimePopup && (
            <div className="time-slot-popup">
              {availableTimes.map((time) => (
                <button
                  key={time}
                  className="time-slot"
                  onClick={() => handleTimeSelect(time)}
                >
                  {time}
                </button>
              ))}
            </div>
          )}

          <button
            type="submit"
            className="booking-form-button"
            disabled={loading || !form.time}
          >
            {loading ? "Booking..." : "Book Appointment"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingForm;
