import React, { useState } from 'react';
import axios from 'axios';
import './BookingForm.css'; // Import the CSS file

const BookingForm = () => {
  // State for form fields
  const [form, setForm] = useState({
    name: '',
    contact: '',
    date: '',
    time: '',
  });

  // State for loading, success, and error messages
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  // Update state when an input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prevForm) => ({
      ...prevForm,
      [name]: value,
    }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    setError('');

    try {
      // Send POST request to your backend API
      const response = await axios.post('http://localhost:5000/api/appointments', form);
      if (response && response.data) {
        setMessage('Appointment booked successfully!');
        // Reset the form fields
        setForm({
          name: '',
          contact: '',
          date: '',
          time: '',
        });
      } else {
        setError('Something went wrong! Please try again.');
      }
    } catch (err) {
      console.error('Error booking appointment:', err);
      if (err.response && err.response.data && err.response.data.message) {
        setError(err.response.data.message);
      } else {
        setError('Failed to book appointment. Please try again later.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="booking-form-container">
      <div className="booking-form-wrapper">
        <h1 className="booking-form-title">Appointment Booking System</h1>
        <h2 className="booking-form-subtitle">Book an Appointment</h2>

        {message && <div className="booking-form-message success">{message}</div>}
        {error && <div className="booking-form-message error">{error}</div>}

        <form onSubmit={handleSubmit} className="booking-form">
          <div className="form-group">
            <label>Full Name</label>
            <input
              type="text"
              name="name"
              value={form.name}
              onChange={handleChange}
              placeholder="Enter your name"
              required
            />
          </div>

          <div className="form-group">
            <label>Contact</label>
            <input
              type="text"
              name="contact"
              value={form.contact}
              onChange={handleChange}
              placeholder="Enter your contact number"
              required
            />
          </div>

          <div className="form-group">
            <label>Appointment Date</label>
            <input
              type="date"
              name="date"
              value={form.date}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Time Slot</label>
            <input
              type="text"
              name="time"
              value={form.time}
              onChange={handleChange}
              placeholder="Enter time (e.g., 10:00 AM)"
              required
            />
          </div>

          <button type="submit" className="booking-form-button" disabled={loading}>
            {loading ? 'Booking...' : 'Book Appointment'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingForm;
