import { useState } from "react";
import axios from "axios";

function BookingForm() {
    const [form, setForm] = useState({ name: "", contact: "", date: "", time: "" });
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState(null);
    const [error, setError] = useState(null);

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setMessage(null);
        setError(null);

        try {
            const response = await axios.post("http://localhost:5000/api/appointments", form);
            
            if (response && response.data) {
                setMessage("✅ Appointment booked successfully!");
                setForm({ name: "", contact: "", date: "", time: "" }); // Clear form
            } else {
                setError("Something went wrong! Please try again.");
            }
        } catch (error) {
            console.error("Error booking appointment:", error);
            if (error.response) {
                setError(error.response.data.message || "Error from server");
            } else if (error.request) {
                setError("No response from server. Check if backend is running.");
            } else {
                setError("Error: " + error.message);
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="max-w-lg mx-auto mt-10 p-6 bg-white shadow-lg rounded-lg border">
            <h2 className="text-2xl font-semibold text-center text-gray-700 mb-4">Book an Appointment</h2>

            {message && <p className="bg-green-100 text-green-700 p-2 rounded mb-3">{message}</p>}
            {error && <p className="bg-red-100 text-red-700 p-2 rounded mb-3">{error}</p>}

            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block text-gray-700 font-medium">Full Name</label>
                    <input 
                        name="name"
                        value={form.name}
                        onChange={handleChange}
                        placeholder="Enter your name"
                        required
                        className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-400"
                    />
                </div>

                <div>
                    <label className="block text-gray-700 font-medium">Contact</label>
                    <input 
                        name="contact"
                        value={form.contact}
                        onChange={handleChange}
                        placeholder="Enter your contact number"
                        required
                        className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-400"
                    />
                </div>

                <div>
                    <label className="block text-gray-700 font-medium">Appointment Date</label>
                    <input 
                        name="date"
                        type="date"
                        value={form.date}
                        onChange={handleChange}
                        required
                        className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-400"
                    />
                </div>

                <div>
                    <label className="block text-gray-700 font-medium">Time Slot</label>
                    <input 
                        name="time"
                        value={form.time}
                        onChange={handleChange}
                        placeholder="Enter time (e.g., 10:00 AM)"
                        required
                        className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-400"
                    />
                </div>

                <button 
                    type="submit"
                    className={`w-full p-2 text-white bg-blue-500 rounded hover:bg-blue-600 transition duration-200 ${
                        loading ? "opacity-50 cursor-not-allowed" : ""
                    }`}
                    disabled={loading}
                >
                    {loading ? "Booking..." : "Book Appointment"}
                </button>
            </form>
        </div>
    );
}

export default BookingForm;
