import React, { useState } from "react";
import Calendar from "react-calendar";
import Modal from "react-modal";
import "react-calendar/dist/Calendar.css";
import "../compon/MyAppointments.css";

const MyAppointments = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  // Sample booked appointments
  const bookedAppointments = {
    "2024-10-10": { time: "10:00 AM", details: "Dental Checkup" },
    "2024-10-15": { time: "2:30 PM", details: "Eye Consultation" },
  };

  const handleDateClick = (date) => {
    const formattedDate = date.toISOString().split("T")[0];
    if (bookedAppointments[formattedDate]) {
      setSelectedDate(formattedDate);
      setModalIsOpen(true);
    }
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const cancelAppointment = () => {
    alert(`Appointment on ${selectedDate} canceled!`);
    setModalIsOpen(false);
  };

  return (
    <div className="calendar-container">
      <Calendar
        onClickDay={handleDateClick}
        tileClassName={({ date }) => {
          const formattedDate = date.toISOString().split("T")[0];
          return bookedAppointments[formattedDate] ? "booked-date" : "";
        }}
      />

      {selectedDate && (
        <Modal
          isOpen={modalIsOpen}
          onRequestClose={closeModal}
          className="modal-content"
          overlayClassName="modal-overlay"
        >
          <h2>Appointment Details</h2>
          <p><strong>Date:</strong> {selectedDate}</p>
          <p><strong>Time:</strong> {bookedAppointments[selectedDate]?.time}</p>
          <p><strong>Details:</strong> {bookedAppointments[selectedDate]?.details}</p>
          <button className="cancel-btn" onClick={cancelAppointment}>Cancel Appointment</button>
          <button className="close-btn" onClick={closeModal}>Close</button>
        </Modal>
      )}
    </div>
  );
};

export default MyAppointments;
