import React, { useState, useEffect } from "react";
import axios from "axios";
import "./BookingForm.css";

const BookingForm = () => {
  const [form, setForm] = useState({
    name: "",
    contact: "",
    date: "",
    time: "",
    reason: "",
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [reasonError, setReasonError] = useState("");
  const [bookedSlots, setBookedSlots] = useState({});
  const [availableTimes, setAvailableTimes] = useState([]);
  const [showTimePopup, setShowTimePopup] = useState(false);

  const timeOptions = [
    "9:00 AM",
    "9:30 AM",
    "10:00 AM",
    "10:30 AM",
    "11:00 AM",
    "11:30 AM",
    "12:00 PM",
    "12:30 PM",
    "3:00 PM",
    "3:30 PM",
    "4:00 PM",
    "4:30 PM",
    "5:00 PM",
  ];

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/booked-slots")
      .then((response) => setBookedSlots(response.data))
      .catch((error) => console.error("Error fetching booked slots:", error));
  }, []);

  // Validation for phone number
  const validatePhoneNumber = (phoneNumber) => {
    const phoneRegex = /^0\d{9}$/;
    return phoneRegex.test(phoneNumber);
  };

  // Validation for the reason field (no special characters)
  const validateReason = (reason) => {
    const reasonRegex = /^[a-zA-Z0-9 ]*$/; // Alphanumeric and spaces only
    return reasonRegex.test(reason);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    setError("");
    setReasonError("");

    // Validate phone number
    if (!validatePhoneNumber(form.contact)) {
      setError(
        "❌ Invalid phone number. Ensure it starts with 0, has 10 digits, and is not already used."
      );
      setLoading(false);
      setTimeout(() => setError(""), 4000);
      return;
    }

    // Validate reason
    if (!validateReason(form.reason)) {
      setReasonError("❌ Reason cannot contain special characters.");
      setLoading(false);
      setTimeout(() => setReasonError(""), 4000);
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:5000/api/appointments",
        form
      );

      if (response && response.data) {
        setMessage("✅ Appointment booked successfully!");
        setBookedSlots((prev) => ({
          ...prev,
          [form.date]: [...(prev[form.date] || []), form],
        }));
        setForm({ name: "", contact: "", date: "", time: "", reason: "" });

        setTimeout(() => setMessage(""), 4000);
      }
    } catch (err) {
      setError(
        err.response?.data.message ||
          "❌ Failed to book appointment. Please try again later."
      );
      setTimeout(() => setError(""), 4000);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="booking-form-container">
      <div className="booking-form-wrapper">
        <h1 className="booking-form-title">Appointment Booking System</h1>
        <h2 className="booking-form-subtitle">Book an Appointment</h2>

        {message && (
          <div className="booking-form-message success">{message}</div>
        )}
        {error && <div className="booking-form-message error">{error}</div>}
        {reasonError && (
          <div className="booking-form-message error">{reasonError}</div>
        )}

        <form onSubmit={handleSubmit} className="booking-form">
          <div className="form-group">
            <label>Client Name</label>
            <input
              type="text"
              name="name"
              value={form.name}
              onChange={(e) => setForm({ ...form, name: e.target.value })}
              placeholder="Enter your name"
              required
            />
          </div>

          <div className="form-group">
            <label>Contact</label>
            <input
              type="text"
              name="contact"
              value={form.contact}
              onChange={(e) => {
                const value = e.target.value.replace(/\D/g, "").slice(0, 10);
                setForm({ ...form, contact: value });
              }}
              placeholder="Enter your contact number (e.g., 0123456789)"
              maxLength="10"
              required
            />
          </div>

          <div className="form-group">
            <label>Reason for Booking</label>
            <input
              type="text"
              name="reason"
              value={form.reason}
              onChange={(e) => {
                const value = e.target.value;
                if (validateReason(value)) {
                  setForm({ ...form, reason: value });
                  setReasonError(""); // Clear error if valid
                } else {
                  setReasonError("❌ Reason cannot contain special characters.");
                }
              }}
              placeholder="Booking Reason (letters, numbers, and spaces only)"
              required
            />
          </div>

          <div className="form-group">
            <label>Appointment Date</label>
            <input
              type="date"
              name="date"
              value={form.date}
              onChange={(e) =>
                setForm({ ...form, date: e.target.value })
              }
              required
            />
          </div>

          <div className="form-group">
            <label>Time Slot</label>
            <input
              type="text"
              name="time"
              value={form.time}
              readOnly
              onClick={() => setShowTimePopup(true)}
              placeholder="Click to select a time slot"
              required
            />
          </div>

          {showTimePopup && (
            <div className="time-slot-popup">
              {availableTimes.map((time) => (
                <button
                  key={time}
                  className="time-slot"
                  onClick={() =>
                    setForm({ ...form, time }) || setShowTimePopup(false)
                  }
                >
                  {time}
                </button>
              ))}
            </div>
          )}

          <button
            type="submit"
            className="booking-form-button"
            disabled={loading || !form.time}
          >
            {loading ? "Booking..." : "Book Appointment"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingForm;
