import React, { useState, useEffect } from "react";
import axios from "axios";
import "./BookingForm.css";

const BookingForm = () => {
  const [form, setForm] = useState({ name: "", contact: "", date: "", time: "" });
  const [availableSlots, setAvailableSlots] = useState({});
  const [timeOptions, setTimeOptions] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3000/api/available-slots").then((response) => {
      setAvailableSlots(response.data);
    });
  }, []);

  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    setForm((prev) => ({ ...prev, date: selectedDate }));
    setTimeOptions(availableSlots[selectedDate] || []);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post("http://localhost:3000/api/appointments", form).then(() => { 
      alert("Appointment booked successfully!");
    }).catch(() => {
      alert("Error booking appointment.");
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Date:</label>
      <input type="date" name="date" onChange={handleDateChange} required />

      <label>Time:</label>
      <select name="time" onChange={(e) => setForm({ ...form, time: e.target.value })} required>
        {timeOptions.map((time) => (
          <option key={time} value={time}>{time}</option>
        ))}
      </select>

      <button type="submit">Book Appointment</button>
    </form>
  );
};

export default BookingForm;
