import React, { useState, useEffect } from "react";
import axios from "axios";
import "./BookingForm.css";

const BookingForm = () => {
  const [form, setForm] = useState({
    name: "",
    contact: "",
    reason: "",
    date: "",
    time: "",
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [bookedSlots, setBookedSlots] = useState({});
  const [availableDates, setAvailableDates] = useState([]);
  const [availableTimes, setAvailableTimes] = useState([]);
  const [showTimePopup, setShowTimePopup] = useState(false);

  const timeOptions = [
    "9:00 AM",
    "9:30 AM",
    "10:00 AM",
    "10:30 AM",
    "11:00 AM",
    "11:30 AM",
    "12:00 PM",
    "12:30 PM",
    "3:00 PM",
    "3:30 PM",
    "4:00 PM",
    "4:30 PM",
    "5:00 PM",
  ];

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/booked-slots")
      .then((response) => setBookedSlots(response.data))
      .catch((error) => console.error("Error fetching booked slots:", error));

    axios
      .get("http://localhost:5000/api/available-dates")
      .then((response) => setAvailableDates(response.data))
      .catch((error) =>
        console.error("Error fetching available dates:", error)
      );
  }, []);

  const validatePhoneNumber = (phoneNumber) => {
    const phonePattern = /^0\d{9}$/; // Ensure phone starts with 0 and is exactly 10 digits long
    return phonePattern.test(phoneNumber); // Validates phone number
  };

  const handleContactChange = (e) => {
    const value = e.target.value;
    
    // Only allow numeric input and ensure it starts with 0 and has at most 10 digits
    if (/[^0-9]/.test(value)) {
      return; // Prevent non-numeric characters
    }
    
    if (value.length > 10) return; // Limit to 10 digits
    
    setForm({ ...form, contact: value });
  };

  const handleNameChange = (e) => {
    const value = e.target.value;

    // Only allow uppercase, lowercase letters, and spaces
    if (/[^a-zA-Z\s]/.test(value)) {
      return; // Prevent any characters other than letters and spaces
    }

    setForm({ ...form, name: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    setError("");

    // Validate phone number
    if (!validatePhoneNumber(form.contact)) {
      setError(
        "❌ Invalid phone number. Must start with 0, have 10 digits, and be unique."
      );
      setLoading(false);
      setTimeout(() => setError(""), 4000);
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:5000/api/appointments",
        form
      );

      if (response && response.data) {
        setMessage("✅ Appointment booked successfully!");
        setBookedSlots((prev) => ({
          ...prev,
          [form.date]: [...(prev[form.date] || []), form],
        }));
        setForm({ name: "", contact: "", reason: "", date: "", time: "" });
        setTimeout(() => setMessage(""), 4000);
      }
    } catch (err) {
      setError(
        err.response?.data.message ||
          "❌ Failed to book appointment. Please try again later."
      );
      setTimeout(() => setError(""), 4000);
    } finally {
      setLoading(false);
    }
  };

  const handleDateChange = (e) => {
    const selectedDate = e.target.value;
    const day = new Date(selectedDate).getDay();

    if (day === 0 || day === 6) {
      setError("❌ Weekends are not allowed. Please choose a weekday.");
      setForm({ ...form, date: "" });
      setTimeout(() => setError(""), 4000);
    } else {
      setForm({ ...form, date: selectedDate });
      const bookedTimes = bookedSlots[selectedDate]
        ? bookedSlots[selectedDate].map((slot) => slot.time)
        : [];
      const available = timeOptions.filter((time) => !bookedTimes.includes(time));
      setAvailableTimes(available);
    }
  };

  const handleTimeSelect = (time) => {
    setForm({ ...form, time });
    setShowTimePopup(false);
    setTimeout(() => setShowTimePopup(false), 4000);
  };

  const validateReason = (reason) => /^[a-zA-Z0-9 ]*$/.test(reason);

  return (
    <div className="booking-form-container">
      <div className="booking-form-wrapper">
        <h1 className="booking-form-title">Appointment Booking System</h1>
        <h2 className="booking-form-subtitle">Book an Appointment</h2>

        {message && (
          <div className="booking-form-message success">{message}</div>
        )}
        {error && <div className="booking-form-message error">{error}</div>}

        <form onSubmit={handleSubmit} className="booking-form">
          <div className="form-group">
            <label>Client Name</label>
            <input
              type="text"
              name="name"
              value={form.name}
              onChange={handleNameChange} // Using the new handler
              placeholder="Enter your name"
              required
            />
          </div>

          <div className="form-group">
            <label>Contact</label>
            <input
              type="text"
              name="contact"
              value={form.contact}
              onChange={handleContactChange} // Using the new handler
              placeholder="Enter your contact number(eg:- 0123456789) "
              required
            />
          </div>

          <div className="form-group">
            <label>Reason for Booking</label>
            <input
              type="text"
              name="reason"
              value={form.reason}
               
              onChange={(e) => {
                if (validateReason(e.target.value)) {
                  setForm({ ...form, reason: e.target.value });
                } else {
                  setError("❌ Special characters are not allowed.");
                  setTimeout(() => setError(""), 4000);
                }
              }}
              placeholder="Booking Reason(Avoid Special Charactors)"
              required
            />
          </div>

          <div className="form-group">
            <label>Appointment Date</label>
            <input
              type="date"
              name="date"
              value={form.date}
              onChange={handleDateChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Time Slot</label>
            <input
              type="text"
              name="time"
              value={form.time}
              readOnly
              onClick={() => setShowTimePopup(true)}
              placeholder="Click to select a time slot"
              required
            />
          </div>

          {showTimePopup && (
            <div className="time-slot-popup">
              {availableTimes.map((time) => (
                <button
                  key={time}
                  className="time-slot"
                  onClick={() => handleTimeSelect(time)}
                >
                  {time}
                </button>
              ))}
            </div>
          )}

          <button
            type="submit"
            className="booking-form-button"
            disabled={loading || !form.time}
          >
            {loading ? "Booking..." : "Book Appointment"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default BookingForm;
