import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Profile = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        const { data } = await axios.get("http://localhost:5000/auth/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(data);
      } catch (error) {
        alert("Error fetching profile. Please try again later.");
      }
    };

    fetchProfile();
  }, []);

  if (!user) return <p>Loading...</p>;

  return (
    <div>
      <h2>Welcome, {user.name}</h2>
      <p>Email: {user.email}</p>
      <h3>Your Appointments:</h3>
      <ul>
        {user.appointments?.length > 0 ? (
          user.appointments.map((appt, index) => (
            <li key={index}>{appt}</li>
          ))
        ) : (
          <p>No appointments found.</p>
        )}
      </ul>
      <button onClick={() => navigate("/booking")}>Book New Appointment</button>
    </div>
  );
};

export default Profile;
