import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [appointments, setAppointments] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        const { data } = await axios.get("http://localhost:5000/auth/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(data);

        // Fetch appointments for this user from the booked slots route
        const appointmentData = await axios.get("http://localhost:5000/appointments/booked-slots", {
          headers: { Authorization: `Bearer ${token}` },
        });

        // If appointments are grouped by date, save them in state
        setAppointments(appointmentData.data);
      } catch (error) {
        console.error("Error fetching profile:", error);
        alert("Error fetching profile. Please try again later.");
        navigate("/login");
      }
    };

    fetchProfile();
  }, [navigate]);

  if (!user) return <p>Loading...</p>;

  return (
    <div>
      <h2>Welcome, {user.name}</h2>
      <p>Email: {user.email}</p>
      <h3>Your Appointments:</h3>
      {Object.keys(appointments).length > 0 ? (
        Object.entries(appointments).map(([date, appointmentsForDate], index) => (
          <div key={index}>
            <h4>{new Date(date).toLocaleDateString()}</h4>
            <ul>
              {appointmentsForDate.map((appt, i) => (
                <li key={i}>
                  {appt.name} - {appt.reason} at {appt.time}
                </li>
              ))}
            </ul>
          </div>
        ))
      ) : (
        <p>No appointments found.</p>
      )}
      <button onClick={() => navigate("/booking")}>Book New Appointment</button>
    </div>
  );
};

export default Profile;
