import { useState } from "react";
import axios from "axios";

const Register = () => {
    const [formData, setFormData] = useState({
        fullName: "",
        email: "",
        phone: "",
        age: "",
        city: "",
        password: ""
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
    
        try {
            const response = await axios.post("http://localhost:5000/api/register", formData);
            alert(response.data.message);  // Show success message
            setFormData({ fullName: "", email: "", phone: "", age: "", city: "", password: "" });
        } catch (error) {
            // Check if error.response is available (i.e., if it's an HTTP error)
            if (error.response) {
                alert("Registration failed: " + error.response.data.message);
            } else {
                alert("Registration failed: Unknown error");
            }
        }
    };
    

    return (
        <form onSubmit={handleSubmit} className="p-4 max-w-md mx-auto border rounded">
            <h2 className="text-xl font-bold mb-4">Register</h2>
            <input type="text" name="fullName" placeholder="Full Name" required onChange={handleChange} value={formData.fullName} className="block w-full p-2 mb-2 border rounded" />
            <input type="email" name="email" placeholder="Email" required onChange={handleChange} value={formData.email} className="block w-full p-2 mb-2 border rounded" />
            <input type="text" name="phone" placeholder="Phone" required onChange={handleChange} value={formData.phone} className="block w-full p-2 mb-2 border rounded" />
            <input type="number" name="age" placeholder="Age" required onChange={handleChange} value={formData.age} className="block w-full p-2 mb-2 border rounded" />
            <input type="text" name="city" placeholder="City" required onChange={handleChange} value={formData.city} className="block w-full p-2 mb-2 border rounded" />
            <input type="password" name="password" placeholder="Password" required onChange={handleChange} value={formData.password} className="block w-full p-2 mb-2 border rounded" />
            <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">Register</button>
        </form>
    );
};

export default Register;
