import { useState } from "react";
import axios from "axios";

function BookingForm() {
    const [form, setForm] = useState({ name: "", contact: "", date: "", time: "" });

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:5000/api/appointments", form);
            
            // Check if response exists before accessing data
            if (response && response.data) {
                alert("Appointment booked successfully!");
            } else {
                alert("Something went wrong! Please try again.");
            }
        } catch (error) {
            console.error("Error booking appointment:", error);
            
            // Handle different types of errors
            if (error.response) {
                alert(error.response.data.message || "Error from server");
            } else if (error.request) {
                alert("No response from server. Check if backend is running.");
            } else {
                alert("Error: " + error.message);
            }
        }
    };
    

    return (
        <form onSubmit={handleSubmit}>
            <input name="name" onChange={handleChange} placeholder="Name" required />
            <input name="contact" onChange={handleChange} placeholder="Contact" required />
            <input name="date" type="date" onChange={handleChange} required />
            <input name="time" onChange={handleChange} placeholder="Time (e.g., 10:00 AM)" required />
            <button type="submit">Book Appointment</button>
        </form>
    );
}

export default BookingForm;
